select.columns:
capuntes.cuenta,
ccuentas.nombre,
NVL(SUM(
(
CASE WHEN ('01-${period}-${ejerci}' > capuntes.fecha OR
(capuntes.period = 0 AND capuntes.asient = 0 AND capuntes.fecha = '01-01-${ejerci}')) THEN debe  ELSE 0 END
)-(
CASE WHEN ('01-${period}-${ejerci}' > capuntes.fecha OR
(capuntes.period = 0 AND capuntes.asient = 0 AND capuntes.fecha = '01-01-${ejerci}')) THEN haber ELSE 0 END
)
), 0) ape,
NVL(SUM(CASE WHEN capuntes.fecha >= '01-${period}-${ejerci}' THEN capuntes.debe ELSE 0 END), 0) debe,
NVL(SUM(CASE WHEN capuntes.fecha >= '01-${period}-${ejerci}' THEN capuntes.haber ELSE 0 END), 0) haber,
NVL(SUM(
(
(
CASE WHEN ('01-${period}-${ejerci}' > capuntes.fecha OR
(capuntes.period = 0 AND capuntes.asient = 0 AND capuntes.fecha = '01-01-${ejerci}')) THEN debe  ELSE 0 END
)-(
CASE WHEN ('01-${period}-${ejerci}' > capuntes.fecha OR
(capuntes.period = 0 AND capuntes.asient = 0 AND capuntes.fecha = '01-01-${ejerci}')) THEN haber ELSE 0 END
)
)+(
CASE WHEN capuntes.fecha >= '01-${period}-${ejerci}' THEN capuntes.debe ELSE 0 END
)-(
CASE WHEN capuntes.fecha >= '01-${period}-${ejerci}' THEN capuntes.haber ELSE 0 END
)
),0) cierre
